mapper.select:
select d.id, d.name, d.category_id, price, image, description, d.status, d.update_time, c.name category_name from dish d left outer join category c on d.category_id = c.id
and d.name like concat('%', #{name}, '%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.create_time desc

mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status},
where id = #{id}
